config.onRenderModuleFn:
module.runOnceOnLoad(function(){

    module.getTextField().getMediaBrowser(function(browser){

        var textarea=null;


        var btn=null;
        browser.addToolbarBtn({
    				"class": "add-youtube combined-browser",
    				events: {
    					click: function() {


    					    if(textarea){

    					        var content=textarea.value;
    					        if(content.trim()!=""){
    					            console.log(content);

        					        HTMLMediaContentRenderer.ParseTextInputExternalVideos(content, function(list){

        					           console.log(list);
        					           if(list.length>0){
        					               module.getTextField().setValue((module.getTextField().getValue()+"\n"+list[0].html).trim());
        					           }

        					           //

        					        })

    					        }

    					        btn.removeClass('active');
    					        textarea.parentNode.removeChild(textarea);
    					        textarea=null;
    					        return;
    					    }

    					    textarea=module.getTextField().getElement().insertBefore(
    					         (new Element('textarea', {
    					             "class":"text-input show-anyway with-2-lines",
    					             "placeholder":"paste youtube embed code or url"
    					         })),
    					         module.getTextField().getInputElement()
    					    );

    					    textarea.focus();

    					    btn.addClass('active');



    					},
    				}
    			},function(button, popover){
    			    btn=button; //store reference
    			});

    });

});
